            function validateDecimal(form) {
                var bValid = true;
                var focusField = null;
                var i = 0;
                var fields = new Array();
                oFloat = new FloatValidations();
                for (x in oFloat) {
                	var field = form[oFloat[x][0]];

                    if (field.type == 'text' ||
                        field.type == 'textarea' ||
                        field.type == 'select-one' ||
                        field.type == 'radio') {

                    	var value;

                        if (field.type == "select-one") {
                                var si = field.selectedIndex;
                                value = field.options[si].value;
                        } else {
                                
                                value = field.value;
								value = value.replace(/\./g, '');
								value = value.replace(/,/g, '.');
                        }

                        if (value.length > 0) {

	                        var iValue = parseFloat(value);
	                    //    if (isNaN(iValue)) {
        				if (isNaN(value) || value <= parseFloat(0)) {
	                            if (i == 0) {
	                                focusField = field;
	                            }
	                            fields[i++] = oFloat[x][1];
	                            bValid = false;
	                        }
                        }
                    }
                }
                if (fields.length > 0) {
                   focusField.focus();
                   alert(fields.join('\n'));
                }
                return bValid;
            }

