                return bValid;
            }

	    	function validacaoNumeroSelo(value){
	        	var achou = false;
	
                if (value.length > 0) {

                 	if (isNaN(value)) {
		               	var iValue = parseInt(value);                 		
		               	if(iValue == 0){
		               		achou = true;
		               	}
                    }else{
                    	achou = false;
                    }
                }
				return achou;
			}

	function validateMatricula(form) {
                var bValid = true;
                var focusField = null;
                var i = 0;
                var fields = new Array();
                oMatricula = new matricula();
                for (x in oMatricula) {
                    if ((form[oMatricula[x][0]].type == 'text' ||
                         form[oMatricula[x][0]].type == 'textarea') &&
                        (form[oMatricula[x][0]].value.length > 0)) {
                        if (!validacaoMatricula(form[oMatricula[x][0]].value)) {
                            if (i == 0) {
                                focusField = form[oMatricula[x][0]];
                            }
                            fields[i++] = oMatricula[x][1];
                            bValid = false;
                        }
                    }
                }
                if (fields.length > 0) {
                    focusField.focus();
                    alert(fields.join('\n'));
                }
                return bValid;
            }

	function validacaoMatricula(c){
		s = c;
		var c = s.substr(0,7);
		var dv = s.substr(7,1);
		var soma = 0;
		var resto = 0;
		var digito = 0;

		if (s.length != 8) {
                 return false;
                }
	        else {

		  soma = (c.charAt(0)* 2) + (c.charAt(1)* 7) + (c.charAt(2)* 6) + (c.charAt(3) * 5) + (c.charAt(4) * 4) + (c.charAt(5) * 3) + (c.charAt(6) * 2);
		  soma = soma * 10;
		  resto = (soma % 11);

		  if (resto == 10){
		    digito = 0;
		  }
		  else {
		    digito = resto;
		  }

		  if (dv != digito) {
		    return false;
		  }
		  else {
      		    return true;
		  }
	        }
	}

                    function validateBigInteger(form) {
                        var bValid = true;
                        var focusField = null;
                        var i = 0;
                        var fields = new Array();
                        oBigInteger = new BigIntegerValidations();
                        for (x in oBigInteger) {
                                var field = form[oBigInteger[x][0]];

                            if (field.type == 'text' ||
                                field.type == 'textarea' ||
                                field.type == 'select-one' ||
                                field.type == 'radio') {

                                var value;

                                if (field.type == "select-one") {
                                        var si = field.selectedIndex;
                                        value = field.options[si].value;
                                } else {
                                        value = trim(field.value);
                                        field.value = value;
                                }

                                if (value.length > 0) {

                                        //var iValue = parseInt(value);
                                        if (isNaN(value) || value.indexOf('.') != -1 || !(value > 0 && value <= 99999999999999999999)) {   
                                        
                                            if (i == 0) {
                                                focusField = field;
                                            }
                                            fields[i++] = oBigInteger[x][1];
                                            bValid = false;
                                       }

                               }
                            }
                        }
                        if (fields.length > 0) {
                           focusField.focus();
                           alert(fields.join('\n'));
                        }
                        return bValid;
                    }
                    
       function trim(inputString) {
					   // Removes leading and trailing spaces from the passed string. Also removes
				   	   // consecutive spaces and replaces it with one space. If something besides
					   // a string is passed in (null, custom object, etc.) then return the input.

					   if (typeof inputString != "string") { return inputString; }
				   	      var retValue = inputString;
		      			  var ch = retValue.substring(0, 1);
						   while (ch == " ") { // Check for spaces at the beginning of the string
							retValue = retValue.substring(1, retValue.length);
					        ch = retValue.substring(0, 1);
				   	   }

					   ch = retValue.substring(retValue.length-1, retValue.length);

					   while (ch == " ") { // Check for spaces at the end of the string
					      retValue = retValue.substring(0, retValue.length-1);
		    	  	      ch = retValue.substring(retValue.length-1, retValue.length);
	   	   	}

	   		while (retValue.indexOf("  ") != -1) { // Note that there are two spaces in the string - look for multiple spaces within the string
	      	retValue = retValue.substring(0, retValue.indexOf("  ")) + retValue.substring(retValue.indexOf("  ")+1, retValue.length); // Again, there are two spaces in each of the strings
   	   }

	   	return retValue; // Return the trimmed string back to the user
	} // Ends the "trim" function
