      then c := c + sc_linha.imsb_qteconomia;
   END IF;
   END LOOP;
 /*  texto := 'R ' || cast(r AS TEXT) || 'C ' c; */
   RETURN 'R '  || cast(r AS TEXT) ; /* || 'C ' || cast(c  AS TEXT) ; */
END;
 
$$;


