   l_linha  cadastro.logradouro%ROWTYPE; 
   lt_linha cadastro.logradouro_tipo%ROWTYPE;   
   b_linha  cadastro.bairro%ROWTYPE;   
   m_linha  cadastro.municipio%ROWTYPE;   
   lc_linha cadastro.logradouro_cep%ROWTYPE; 
   c_linha  cadastro.cep%ROWTYPE; 
BEGIN
   SELECT * INTO t_linha FROM cadastro.imovel i
   WHERE imov_id = imovel;
   if t_linha.imov_dscomplementoendereco is null
      then t_linha.imov_dscomplementoendereco = ' ';
   end if;
   Select * into lb_linha from cadastro.logradouro_bairro lb
   where t_linha.lgbr_id = lb.lgbr_id;
   Select * into l_linha from cadastro.logradouro l
   where lb_linha.logr_id = l.logr_id;
   Select * into lt_linha from cadastro.logradouro_tipo lt
   where l_linha.lgtp_id = lt.lgtp_id;
   Select * into b_linha from cadastro.bairro b
   where lb_linha.bair_id = b.bair_id;
   Select * into m_linha from cadastro.municipio m
   where b_linha.muni_id = m.muni_id;
   Select * into lc_linha from cadastro.logradouro_cep lc
   where t_linha.lgcp_id = lc.lgcp_id;
   Select * into c_linha from cadastro.cep c
   where lc_linha.cep_id = c.cep_id;
   RETURN 
--cast(t_linha.imov_id as text) || ' ' || cast(t_linha.lgbr_id as text) || ' ' || lt_linha.lgtp_dslogradourotipo || ' ' || l_linha.logr_nmlogradouro;
 lt_linha.lgtp_dslogradourotipo 
 || ' ' || l_linha.logr_nmlogradouro 
 || ',' || t_linha.imov_nnimovel 
 || ' ' || t_linha.imov_dscomplementoendereco 
 || ' ' || b_linha.bair_nmbairro || ' ' 
 || m_linha.muni_nmmunicipio || ' - ' 
 || c_linha.cep_dsufsigla ||  ' CEP:' || substr(cast(c_linha.cep_cdcep as text), 1,5) || '-' || 
  substr(cast(c_linha.cep_cdcep as text), 6,3);
END;
 
