     INNER JOIN cadastro.municipio E ON D.muni_id = E.muni_id) AS A
   INNER JOIN (SELECT loca_id, SUM(date_part('day', orse_tmencerramento - orse_tmgeracao)) AS qtdDias, count(*) AS qtdOS  
