          WHERE (orse_tmencerramento BETWEEN dataInicial AND dataFinal)
      AND orse_tmgeracao > '20110101' 
      AND atendimentopublico.registro_atendimento.rgat_id = atendimentopublico.ordem_servico.rgat_id 
      AND atendimentopublico.ordem_servico.svtp_id=atendimentopublico.servico_tipo.svtp_id 
      AND svtp_cdservicotipo = 'O' 
      AND (atendimentopublico.ordem_servico.svtp_id=743 OR atendimentopublico.ordem_servico.svtp_id=788 OR atendimentopublico.ordem_servico.svtp_id=794 OR
           atendimentopublico.ordem_servico.svtp_id=875 OR atendimentopublico.ordem_servico.svtp_id=878 OR atendimentopublico.ordem_servico.svtp_id=879 OR
           atendimentopublico.ordem_servico.svtp_id=884 OR atendimentopublico.ordem_servico.svtp_id=886 OR atendimentopublico.ordem_servico.svtp_id=889 OR
           atendimentopublico.ordem_servico.svtp_id=891 OR atendimentopublico.ordem_servico.svtp_id=893 OR atendimentopublico.ordem_servico.svtp_id=894 OR
           atendimentopublico.ordem_servico.svtp_id=900 OR atendimentopublico.ordem_servico.svtp_id=1012 OR atendimentopublico.ordem_servico.svtp_id=1204 OR
           atendimentopublico.ordem_servico.svtp_id=1205)
          GROUP BY loca_id) AS B ON A.loca_id = B.loca_id;

