			+ "ger.nome, " + "uni.id, " + "uni.nome, " + "loc.id, " + "loc.descricao, " + "cas.id, " + "cas.descricao, "
					+ "count(cod.id) as quantidadeSituacao, " + "sum(cod.valorDocumento) as valorSituacao, ";

