	/**
	 * [UC0457] Encerrar Ordem de Servio [SB0006]  Obter Unidade
	 * Repavimentadora do Municpio
	 * 
	 * @author Arthur Carvalho
	 * @date 12/04/2010
	 * 
	 */
	public UnidadeOrganizacional obterUnidadeRepavimentadorAPartirMunicipio(OrdemServico os, String tipoPesquisa) {

		try {

			UnidadeOrganizacional unidadeOrganizacional = null;

			// 1.1. Caso a ordem de servio esteja associada a um registro de
			// atendimento
			if (os.getRegistroAtendimento() != null) {

				// 1.1.1.Caso o endereo do local da ocorrncia do registro de
				// atendimento seja pelo CEP
				if (os.getRegistroAtendimento().getLogradouroCep() != null) {
					tipoPesquisa = ConstantesSistema.PESQUISA_PELO_CEP;
					unidadeOrganizacional = getControladorOrdemServico().obterUnidadeRepavimentadorAPartirMunicipio(os, tipoPesquisa);
				} else {
					tipoPesquisa = ConstantesSistema.PESQUISA_PELO_BAIRRO;
					unidadeOrganizacional = getControladorOrdemServico().obterUnidadeRepavimentadorAPartirMunicipio(os, tipoPesquisa);
				}
			} else {
				tipoPesquisa = ConstantesSistema.PESQUISA_PELO_IMOVEL;
				unidadeOrganizacional = getControladorOrdemServico().obterUnidadeRepavimentadorAPartirMunicipio(os, tipoPesquisa);
			}

			return unidadeOrganizacional;

		} catch (ControladorException ex) {
			throw new FachadaException(ex.getMessage(), ex, ex.getParametroMensagem());
		}
	}

